$(function(){var o=$(window),i=$(window).height();o.on("scroll",function(){var o=$(this).scrollTop(),c=$(".hero");o>c.offset().top-i&&c.css({"background-position":"50% "+(-(o/2)-c.offset().top)+"px"})});var c=$(".click"),n=$(".close");$(c).each(function(o){o++,$(this).on("click",function(){$(".view-"+o).addClass("visible")}),n.on("click",function(){$(".large__view").removeClass("visible")})})});